import React from 'react';

const About = () => {
  return (
    <section className="w-full bg-gradient-to-b from-[#fee7bf] to-[#ffa6b0] py-24 px-4">
      <h2 className="text-center text-[#f17f7f] text-4xl md:text-5xl font-bold uppercase tracking-wide mb-20">
        About Hoop
      </h2>
      <div className="max-w-7xl mx-auto flex flex-col md:flex-row items-center justify-center gap-12 md:gap-20 px-4">
        <div className="relative w-[280px] md:w-[320px]">
          <img src="/Ref/hoop_page/char_1.png" alt="Left Character" className="w-full h-auto object-contain mask-fade-bottom" />
        </div>
        <div className="bg-[#fff4ef] rounded-xl shadow-lg px-8 py-6 max-w-md text-center text-[#444] text-base leading-relaxed">
          <p><strong>HOOP</strong> is a revolutionary NFT collection that brings together the best of gaming, art, and community. Each character is uniquely designed with distinctive traits and personalities that make them truly special.</p>
          <br />
          <p>Join our vibrant community and discover the endless possibilities that await in the HOOP universe. From rare collectibles to exclusive experiences, there's something for everyone in the HOOP ecosystem.</p>
        </div>
        <div className="relative w-[280px] md:w-[320px]">
          <img src="/Ref/hoop_page/char_2.png" alt="Right Character" className="w-full h-auto object-contain mask-fade-bottom" />
        </div>
      </div>
    </section>
  );
};

export default About;
