import React from 'react';

const Banner = () => {
  return (
    <>
      {/* HEADER */}
      <header className="flex justify-between items-center px-6 py-4 border-b border-black/20">
        <img src="/Ref/LaGoqDgB_400x400.jpg" alt="avatar" className="w-10 h-10 rounded-full" />
        <div className="flex-1 mx-4 h-10 bg-[#fee7bf] border border-black/10 rounded-full"></div>
        <button className="bg-[#ffa6b0] text-white font-bold px-4 py-2 rounded-full shadow-inner hover:scale-105 transition">
          BUY HOOP
        </button>
      </header>

      {/* MAIN HOOP LOGO SECTION */}
      <section className="relative h-screen flex items-center justify-center overflow-hidden">
        <h1 className="text-[clamp(4rem,10vw,10rem)] font-extrabold uppercase text-transparent bg-clip-text bg-center bg-cover"
            style={{ backgroundImage: "url('/Ref/GT0hAeXWsAEM4cg.jpg')" }}>
          HOOP
        </h1>
      </section>
    </>
  );
};

export default Banner;
