import React, { useState, useEffect } from 'react';
import { motion, useScroll } from 'framer-motion';

const Banner = () => {
  const [hasScrolled, setHasScrolled] = useState(false);
  const { scrollY } = useScroll();
  
  useEffect(() => {
    const unsubscribe = scrollY.onChange((latest) => {
      if (latest > 50) {
        setHasScrolled(true);
      }
    });
    
    return () => unsubscribe();
  }, [scrollY]);

  return (
    <>
      {/* Header */}
      <header className="flex justify-between items-center px-4 py-3 border-b border-black/20">
        <img src="/Ref/LaGoqDgB_400x400.jpg" className="w-10 h-10 rounded-full" alt="User avatar" />
        <div className="flex-1 mx-4 h-10 bg-[#fee7bf] rounded-full border border-black/10"></div>
        <button className="bg-[#ffa6b0] text-white font-bold px-4 py-2 rounded-full shadow-inner text-sm hover:scale-105 transition">
          BUY HOOP
        </button>
      </header>

      {/* Main HOOP Block */}
      <section className="relative flex items-center justify-center h-screen overflow-hidden">
        <motion.h1 
          className="text-[clamp(4rem,10vw,10rem)] font-extrabold uppercase text-transparent bg-clip-text bg-center bg-cover transition-transform duration-700 will-change-transform"
          style={{
            backgroundImage: "url('/Ref/GT0hAeXWsAEM4cg.jpg')",
            backgroundSize: 'cover',
            backgroundPosition: 'center'
          }}
          initial={{ opacity: 0, y: 300, scale: 0.1 }}
          animate={hasScrolled ? { opacity: 1, y: 0, scale: 1 } : { opacity: 0, y: 300, scale: 0.1 }}
          transition={{
            duration: 2,
            delay: 0,
            ease: "easeOut"
          }}
        >
          HOOP
        </motion.h1>
      </section>
    </>
  );
};

export default Banner;
