import React, { useState, useEffect } from 'react';
import { motion, useScroll } from 'framer-motion';

const Banner = () => {
  const [hasScrolled, setHasScrolled] = useState(false);
  const { scrollY } = useScroll();
  
  useEffect(() => {
    const unsubscribe = scrollY.onChange((latest) => {
      if (latest > 50) {
        setHasScrolled(true);
      }
    });
    
    return () => unsubscribe();
  }, [scrollY]);

  return (
    <>
      {/* Header with image background */}
      <header className="relative">
        <img 
          src="/Ref/hoop_page/top.png" 
          alt="Header background" 
          className="w-full h-16 object-cover"
        />
        
        {/* Transparent buttons overlay */}
        <div className="absolute inset-0 flex justify-between items-center px-3 py-1">
          <div className="w-8 h-8 rounded-full cursor-pointer hover:bg-white/10 transition-colors"></div>
          <div className="flex-1 mx-3 h-8 rounded-full cursor-pointer hover:bg-white/10 transition-colors"></div>
          <button className="bg-transparent text-white font-bold px-3 py-1 rounded-full hover:bg-white/10 transition-colors text-xs">
            BUY HOOP
          </button>
        </div>
      </header>

      {/* Main HOOP Block */}
      <section className="relative flex items-center justify-center h-screen overflow-hidden">
        <motion.div
          className="relative"
          initial={{ opacity: 0, y: 300, scale: 0.1 }}
          animate={hasScrolled ? { opacity: 1, y: 0, scale: 1 } : { opacity: 0, y: 300, scale: 0.1 }}
          transition={{
            duration: 2,
            delay: 0,
            ease: "easeOut"
          }}
        >
          <img 
            src="/Ref/hoop_page/title.png" 
            alt="HOOP Title" 
            className="max-w-full h-auto"
          />
        </motion.div>
      </section>
    </>
  );
};

export default Banner;
