import React from 'react';

const Gallery = () => {
  const galleryImages = [
    '/Ref/forGallery/1.png',
    '/Ref/forGallery/2.png',
    '/Ref/forGallery/3.png',
    '/Ref/forGallery/4.png',
    '/Ref/forGallery/5.png',
    '/Ref/forGallery/6.png'
  ];

  return (
    <section className="relative py-20 px-4 bg-pink-300 text-center overflow-hidden">
      <h2 className="text-4xl font-bold text-white mb-10 font-pixel">GALLERY</h2>
      <div className="relative mx-auto max-w-6xl grid grid-cols-2 md:grid-cols-3 gap-4 px-2 md:px-4">
        {/* Edge blur overlay */}
        <div className="absolute inset-0 pointer-events-none z-10"
             style={{ maskImage: 'linear-gradient(to right, transparent, black 20%, black 80%, transparent)', backdropFilter: 'blur(8px)' }}>
        </div>
        {/* Gallery Items */}
        {galleryImages.map((image, index) => (
          <img key={index} src={image} className="rounded-md shadow-md" alt={`Gallery ${index + 1}`} />
        ))}
      </div>
    </section>
  );
};

export default Gallery;
