import React from 'react';
import { motion } from 'framer-motion';

const SwapAndSocials = () => {
  return (
    <section className="py-16 px-4 text-center">
      <motion.h2 
        className="text-4xl font-bold text-white mb-8"
        initial={{ opacity: 0, y: 50 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true }}
      >
        HOW TO BUY
      </motion.h2>
      
      <div className="flex flex-col md:flex-row items-center justify-center gap-10 max-w-6xl mx-auto">
        
        {/* Swap Widget */}
        <motion.div 
          className="w-[320px] h-[260px] bg-black rounded-lg overflow-hidden shadow-xl"
          initial={{ opacity: 0, x: -50 }}
          whileInView={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
          viewport={{ once: true }}
        >
          <iframe
            src="https://jup.ag/swap?outputCurrency=SOL"
            className="w-full h-full border-none"
            title="Jupiter Swap Widget"
          />
        </motion.div>

        {/* Info Block */}
        <motion.div 
          className="w-[320px] h-[260px] bg-[#f5a1a1] rounded-lg p-6 text-left shadow-lg"
          initial={{ opacity: 0, x: 50 }}
          whileInView={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8, delay: 0.4 }}
          viewport={{ once: true }}
        >
          <h3 className="text-2xl font-bold mb-2 text-white">Step 1:</h3>
          <p className="font-semibold text-xl mb-1 text-white">Setup a wallet</p>
          <p className="text-sm opacity-80 text-white">And take off with $HOOP!</p>
          
          <div className="mt-8">
            <h4 className="text-lg font-bold text-white mb-3">Quick Steps:</h4>
            <ul className="text-white space-y-2">
              <li className="flex items-center">
                <span className="w-2 h-2 bg-white rounded-full mr-3"></span>
                Download Phantom or Solflare wallet
              </li>
              <li className="flex items-center">
                <span className="w-2 h-2 bg-white rounded-full mr-3"></span>
                Add SOL to your wallet
              </li>
              <li className="flex items-center">
                <span className="w-2 h-2 bg-white rounded-full mr-3"></span>
                Connect wallet to Jupiter
              </li>
              <li className="flex items-center">
                <span className="w-2 h-2 bg-white rounded-full mr-3"></span>
                Swap SOL for HOOP tokens
              </li>
            </ul>
          </div>
        </motion.div>
      </div>

      {/* Logos */}
      <motion.div 
        className="flex justify-center gap-4 mt-8"
        initial={{ opacity: 0, y: 30 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, delay: 0.6 }}
        viewport={{ once: true }}
      >
        <div className="w-10 h-10 rounded-full shadow bg-white/20 backdrop-blur-sm flex items-center justify-center">
          <span className="text-white font-bold text-sm">SOL</span>
        </div>
        <div className="w-10 h-10 rounded-full shadow bg-white/20 backdrop-blur-sm flex items-center justify-center">
          <span className="text-white font-bold text-sm">HOOP</span>
        </div>
      </motion.div>
    </section>
  );
};

export default SwapAndSocials;
