import React from 'react';
import { motion } from 'framer-motion';
import ImageLoader from './ImageLoader';
import AnimatedText from './AnimatedText';

const Gallery = () => {
  // Images from Ref/forGallery folder - using absolute paths
  const galleryImages = [
    {
      id: 1,
      src: '/Ref/forGallery/1.png',
      alt: 'HOOP Moment 1'
    },
    {
      id: 2,
      src: '/Ref/forGallery/2.png',
      alt: 'HOOP Moment 2'
    },
    {
      id: 3,
      src: '/Ref/forGallery/3.png',
      alt: 'HOOP Moment 3'
    },
    {
      id: 4,
      src: '/Ref/forGallery/4.png',
      alt: 'HOOP Moment 4'
    },
    {
      id: 5,
      src: '/Ref/forGallery/5.png',
      alt: 'HOOP Moment 5'
    },
    {
      id: 6,
      src: '/Ref/forGallery/6.png',
      alt: 'HOOP Moment 6'
    },
    {
      id: 7,
      src: '/Ref/forGallery/7.png',
      alt: 'HOOP Moment 7'
    },
    {
      id: 8,
      src: '/Ref/forGallery/8.png',
      alt: 'HOOP Moment 8'
    },
    {
      id: 9,
      src: '/Ref/forGallery/9.png',
      alt: 'HOOP Moment 9'
    },
    {
      id: 10,
      src: '/Ref/forGallery/10.png',
      alt: 'HOOP Moment 10'
    },
    {
      id: 11,
      src: '/Ref/forGallery/11.png',
      alt: 'HOOP Moment 11'
    },
    {
      id: 12,
      src: '/Ref/forGallery/12.png',
      alt: 'HOOP Moment 12'
    },
    {
      id: 13,
      src: '/Ref/forGallery/13.png',
      alt: 'HOOP Moment 13'
    },
    {
      id: 14,
      src: '/Ref/forGallery/pic_1.png',
      alt: 'HOOP Moment 14'
    }
  ];

  return (
    <section className="py-20 px-4">
      <div className="max-w-6xl mx-auto">
        <AnimatedText
          delay={0}
          duration={0.8}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold text-white mb-4">
            Gallery
          </h2>
          <p className="text-xl text-gray-300">
            HOOP Moments
          </p>
        </AnimatedText>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 2xl:grid-cols-7 gap-4">
          {galleryImages.map((image, index) => (
            <motion.div
              key={image.id}
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{
                duration: 0.6,
                delay: index * 0.1
              }}
              viewport={{ once: true }}
              className="group relative overflow-hidden rounded-2xl bg-gradient-to-br from-hoop-dark/30 to-hoop-accent/30 backdrop-blur-sm border border-hoop-glow/20"
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              <div className="aspect-square overflow-hidden">
                <ImageLoader
                  src={image.src}
                  alt={image.alt}
                  className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
                />
              </div>
              
              {/* Overlay */}
              <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                <div className="absolute bottom-4 left-4 right-4">
                  <h3 className="text-white font-semibold text-lg">
                    {image.alt}
                  </h3>
                  <p className="text-white/80 text-sm">
                    Community • Creativity • Connection
                  </p>
                </div>
              </div>

              {/* Hover effect overlay */}
              <div className="absolute inset-0 bg-white/5 opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
            </motion.div>
          ))}
        </div>

        {/* Load more button */}
        <AnimatedText
          delay={0.6}
          duration={0.8}
          className="text-center mt-12"
        >
          <button className="bg-gradient-to-r from-hoop-glow/20 to-purple-500/20 hover:from-hoop-glow/30 hover:to-purple-500/30 text-white px-8 py-3 rounded-full font-semibold transition-all duration-300 backdrop-blur-sm border border-hoop-glow/30">
            View More Moments
          </button>
        </AnimatedText>
      </div>
    </section>
  );
};

export default Gallery;
