import React from 'react';
import { motion } from 'framer-motion';

const About = () => {
  return (
    <section className="relative py-32 text-center min-h-screen flex items-center">
      <div className="w-full">
        <motion.h2 
          className="text-5xl font-bold text-[#f17f7f] drop-shadow mb-20 text-center w-full"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          ABOUT HOOP
        </motion.h2>
        
        <div className="relative max-w-7xl mx-auto px-4 w-full flex items-center justify-center">
          {/* Left character - positioned to the left of content */}
          <motion.img 
            src="/Ref/hoop_page/char_1.png" 
            className="w-80 md:w-[500px] mr-4"
            initial={{ opacity: 0, x: -100 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 1, delay: 0.2 }}
            viewport={{ once: true }}
            alt="Left character"
          />
          
          {/* Content area */}
          <div className="relative z-10 max-w-xl">
            <motion.div
              className="bg-white/20 backdrop-blur-sm rounded-xl p-12 shadow-lg"
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.6 }}
              viewport={{ once: true }}
            >
              <p className="text-xl text-gray-800 leading-relaxed">
                HOOP is a revolutionary NFT collection that brings together the best of gaming, 
                art, and community. Each character is uniquely designed with distinctive traits 
                and personalities that make them truly special.
              </p>
              <p className="text-xl text-gray-800 leading-relaxed mt-6">
                Join our vibrant community and discover the endless possibilities that await 
                in the HOOP universe. From rare collectibles to exclusive experiences, 
                there's something for everyone in the HOOP ecosystem.
              </p>
            </motion.div>
          </div>
          
          {/* Right character - positioned to the right of content */}
          <motion.img 
            src="/Ref/hoop_page/char_2.png" 
            className="w-80 md:w-[500px] ml-4"
            initial={{ opacity: 0, x: 100 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 1, delay: 0.4 }}
            viewport={{ once: true }}
            alt="Right character"
          />
        </div>
      </div>
    </section>
  );
};

export default About;
