import React, { useState, useEffect } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import AnimatedText from './AnimatedText';

const Banner = () => {
  const [hasScrolled, setHasScrolled] = useState(false);
  const { scrollY } = useScroll();
  
  useEffect(() => {
    const unsubscribe = scrollY.onChange((latest) => {
      if (latest > 50) {
        setHasScrolled(true);
      }
    });
    
    return () => unsubscribe();
  }, [scrollY]);

  return (
    <section className="relative h-screen flex items-center justify-center overflow-hidden bg-gradient-to-br from-hoop-bg via-hoop-dark to-hoop-accent">
      {/* Background image from Ref */}
      <div className="absolute inset-0 opacity-20">
        <img 
          src="/Ref/GT0hAeXWsAEM4cg.jpg" 
          alt="HOOP Background" 
          className="w-full h-full object-cover"
          onError={(e) => {
            e.target.style.display = 'none';
          }}
        />
      </div>

      {/* Animated background elements */}
      <div className="absolute inset-0">
        <motion.div
          className="absolute top-20 left-20 w-32 h-32 bg-hoop-glow/20 rounded-full blur-xl"
          animate={{
            y: [0, -20, 0],
            scale: [1, 1.1, 1],
          }}
          transition={{
            duration: 4,
            repeat: Infinity,
            ease: "easeInOut"
          }}
        />
        <motion.div
          className="absolute top-40 right-32 w-24 h-24 bg-hoop-glow/20 rounded-full blur-xl"
          animate={{
            y: [0, 15, 0],
            scale: [1, 0.9, 1],
          }}
          transition={{
            duration: 3,
            repeat: Infinity,
            ease: "easeInOut",
            delay: 1
          }}
        />
        <motion.div
          className="absolute bottom-32 left-1/4 w-20 h-20 bg-hoop-glow/20 rounded-full blur-xl"
          animate={{
            y: [0, -10, 0],
            scale: [1, 1.2, 1],
          }}
          transition={{
            duration: 5,
            repeat: Infinity,
            ease: "easeInOut",
            delay: 2
          }}
        />
      </div>

      {/* Main content */}
      <div className="relative z-10 text-center px-4">
        <motion.div
          className="text-6xl md:text-8xl lg:text-9xl font-bold text-white mb-8"
          initial={{ opacity: 0, y: 300, scale: 0.1 }}
          animate={hasScrolled ? { opacity: 1, y: 0, scale: 1 } : { opacity: 0, y: 300, scale: 0.1 }}
          transition={{
            duration: 2,
            delay: 0,
            ease: "easeOut"
          }}
        >
          Welcome to
        </motion.div>
        
        <motion.div
          className="text-7xl md:text-9xl lg:text-[12rem] font-bold text-gradient"
          initial={{ opacity: 0, y: 400, scale: 0.1 }}
          animate={hasScrolled ? { opacity: 1, y: 0, scale: 1 } : { opacity: 0, y: 400, scale: 0.1 }}
          transition={{
            duration: 2.5,
            delay: 0.3,
            ease: "easeOut"
          }}
        >
          HOOP
        </motion.div>

        <AnimatedText
          className="mt-8"
          delay={0.6}
          duration={1}
        >
          <div className="w-24 h-1 bg-gradient-to-r from-hoop-glow to-purple-500 mx-auto rounded-full" />
        </AnimatedText>
      </div>

      {/* Scroll indicator */}
      <motion.div
        className="absolute bottom-8 left-1/2 transform -translate-x-1/2"
        animate={{ y: [0, 10, 0] }}
        transition={{
          duration: 2,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      >
        <div className="w-6 h-10 border-2 border-hoop-glow rounded-full flex justify-center">
          <div className="w-1 h-3 bg-hoop-glow rounded-full mt-2 animate-bounce" />
        </div>
      </motion.div>
    </section>
  );
};

export default Banner;
